def get_disease_info(disease_key):
    """Get disease information by key"""
    return DISEASE_DATABASE.get(disease_key, None)


def is_healthy(disease_key):
    """Check if disease key indicates healthy plant"""
    return 'healthy' in disease_key.lower()


def get_all_diseases():
    """Get list of all disease keys"""
    return list(DISEASE_DATABASE.keys())


def get_confidence_message(confidence, disease_key):
    """
    Returns appropriate message based on confidence level
    
    Parameters:
        confidence: Prediction confidence (0-100)
        disease_key: The predicted disease
    
    Returns:
        Dictionary with warning level and message
    """
    if confidence >= 85:
        return {
            'level': 'high',
            'message': None,
            'color': 'green'
        }
    elif confidence >= 70:
        return {
            'level': 'medium',
            'message': 'The model is fairly confident about this diagnosis, but consider getting a second opinion.',
            'color': 'yellow'
        }
    elif confidence >= 60:
        return {
            'level': 'low',
            'message': 'Low confidence detection. The model has limited data for this condition. Please consult an expert for verification.',
            'color': 'orange'
        }
    else:
        return {
            'level': 'very_low',
            'message': 'Very low confidence. The model does not have enough training data to reliably identify this condition. Please consult a plant expert or agricultural extension office.',
            'color': 'red'
        }